import React from "react";
import "./AllSpots.css";
import { Link } from "react-router-dom";
import SpotCard from "../../components/SpotCard/SpotCard";

class AllSpots extends React.Component {
  handleGetRequest = async () => {
    const response = await fetch(`/api/v1/users/${this.props.userId}/spots`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await response.json();
    console.log("spot", data);
    this.props.setSpots(data);
  };

  componentDidMount() {
    this.handleGetRequest();
  }

  render() {
    console.log("log", this.props.userId);
    return (
      <>
        <div className="spotGrid">
          {this.props.spots.map((spot) => {
            return (
              <>
                <SpotCard
                  mylat={this.props.distance.latitude}
                  mylng={this.props.distance.longitude}
                  // lat={spot.coordinates[0]}
                  // lng={spot.coordinates[1]}
                  id={spot.id}
                  name={spot.name}
                  img={spot.imageCover}
                  spotType={spot.spotType}
                  slug={spot.slug}
                  ratingsAverage={spot.ratingsAverage}
                  ratingsQuantity={spot.ratingsQuantity}
                />
              </>
            );
          })}
        </div>
      </>
    );
  }
}

export default AllSpots;
