import React from "react";
import "../../pages/Spot/Spot.css";
import "../../components/Reviews/Reviews.css";
import "bootstrap/dist/css/bootstrap.min.css";
import Auto from "../../img/Auto.png";
import moment from "moment";
import { FaStar } from "react-icons/fa";

class Reviews extends React.Component {
  render() {
    console.log("props", this.props.reviews);
    return (
      <>
        <div className="reviewContainer">
          <div className="face-stars">
            <img className="face-Img" src={Auto} />

            <div className="star-date">
              <div className="stars">
                {[...Array(5)].map((star, index) => {
                  return (
                    <FaStar
                      size={24}
                      color={this.props.rating >= index ? "#ffc107" : "#e4e5e9"}
                    />
                  );
                })}
              </div>
              <div className="createdAt">
                {moment(this.props.createdAt).format("L")}
              </div>
            </div>
          </div>
          <div className="review">{this.props.review}</div>
        </div>
      </>
    );
  }
}

export default Reviews;
