import React from "react";
import "../../pages/Spot/Spot.css";
import Skate from "../../img/spot-2-cover.jpeg";
import Reviews from "../../components/Reviews/Reviews";
// import GoogleMap from "../../components/Map/Map";
// import { Link } from "react-router-dom";
// import DogCard from "../components/SpotCard";
import { Container, Row, Col } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { getDistance } from "geolib";

class Spot extends React.Component {
  state = {
    spot: { location: {} },
    details: null,
  };
  handleGetRequest = async () => {
    const response = await fetch(`/api/v1/spots/${this.props.match.params.id}`);
    const data = await response.json();
    console.log(data);
    this.setState({ spot: data.data.spot });
    this.props.setSpot(data.data.spot);
  };

  componentDidMount() {
    this.handleGetRequest();
  }
  render() {
    console.log("mySpot", this.state.spot);
    return (
      <div className="mainContainer">
        <div className="name">{this.state.spot.name}</div>
        <div className="container2">
          <img className="spotImg" src={this.state.spot.photo} />
        </div>
        <Container>
          <Row>
            <Col xsm="6">
              <span className="detailsHead">Details</span>
              <div className="type">Spot Type: {this.state.spot.spotType}</div>

              <div className="spot-address">
                Address: {this.state.spot.address}
              </div>
            </Col>
            <Col xsm="6">
              <span className="descriptionHead">Description</span>
              <div className="description">{this.state.spot.description}</div>
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default Spot;
