import React from "react";
import "./CreateSpot.css";
// import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
import { Form, Button } from "react-bootstrap";
class CreateSpot extends React.Component {
  state = {
    name: "",
    address: "",
    select: "",
    description: "",

    coordinates: [],
  };

  getUserGeolocationDetails = async () => {
    var response = await fetch(
      "https://geolocation-db.com/json/697de680-a737-11ea-9820-af05f4014d91"
    );
    const data = await response.json();
    console.log("dataaa", data);
    let latlng = [data.longitude, data.latitude];
    this.setState({ coordinates: latlng });
  };

  handleInputChange = (event) => {
    const { value, name } = event.target;
    this.setState({
      [name]: value,
    });
  };

  //   handleClearForm(e) {

  //     e.preventDefault();
  //     this.setState({
  //       newSpot: {
  //       name: "",
  //       address: "",
  //       select: "",
  //       description: "",

  //       },
  //     })
  // }
  onSubmit = async (event) => {
    event.preventDefault();
    const res = await fetch("api/v1/spots", {
      method: "POST",
      body: JSON.stringify(this.state),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();
    console.log(data);
  };
  componentDidMount() {
    this.getUserGeolocationDetails();
  }

  render() {
    return (
      <div>
        <Form className="form" onSubmit={this.onSubmit}>
          <Form.Group controlId="formName">
            <Form.Label>Name the Spot</Form.Label>
            <Form.Control
              type="text"
              name="name"
              placeholder="Spot Name"
              value={this.state.name}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="formAddress">
            <Form.Label></Form.Label>
            <Form.Control
              type="hidden"
              name="address"
              placeholder="Spot Address"
              value={this.state.coordinates}
            />
          </Form.Group>

          <Form.Group controlId="formSelect">
            <Form.Label>Spot Type</Form.Label>
            <Form.Control
              as="select"
              value={this.state.select}
              onChange={this.handleInputChange}
            >
              <option value="street">street</option>
              <option value="park">skatepark</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="formDiscription">
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="textarea"
              name="description"
              value={this.state.description}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          {/* <FormGroup>
          <Label for="exampleFile">File</Label>
          <Input type="file" name="file" id="exampleFile" />
          <FormText color="muted">
            This is some placeholder block-level help text for the above input.
            It's a bit lighter and easily wraps to a new line.
          </FormText>
        </FormGroup> */}

          <Button>Submit</Button>
        </Form>
      </div>
    );
  }
}

export default CreateSpot;
