import React from "react";
import "./MySpots.css";
import MySpotCard from "../../components/MySpotCard/MySpotCard";
// import Skate from "../../img/spot-2-cover.jpeg";

// import { Container, Row, Col } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";

class MySpots extends React.Component {
  state = {
    currentUser: [],
  };

  handleGetRequest = async () => {
    const res = await fetch("/api/v1/users/me", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();
    console.log(data);
    this.setState({ currentUser: data.data.user.spots });
  };

  componentDidMount() {
    this.handleGetRequest();
    console.log("distance", this.props.distance);
  }
  render() {
    console.log("data", this.state.currentUser);
    return (
      <>
        {this.state.currentUser.map((spot) => {
          return (
            <>
              <MySpotCard
                mylat={this.props.distance.latitude}
                mylng={this.props.distance.longitude}
                lat={spot.coordinates[0]}
                lng={spot.coordinates[1]}
                id={spot.id}
                name={spot.name}
                img={spot.imageCover}
                spotType={spot.spotType}
                slug={spot.slug}
                ratingsAverage={spot.ratingsAverage}
                ratingsQuantity={spot.ratingsQuantity}
              />
            </>
          );
        })}
      </>
    );
  }
}

export default MySpots;
