import React from "react";
import "./CreateSpot.css";
// import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
import { Form, Button } from "react-bootstrap";

// import Geocode from "react-geocode";

class CreateSpot extends React.Component {
  state = {
    name: "",
    address: "",
    spotType: "",
    description: "",
    coordinates: [],
    latitude: null,
    longitude: null,
    userAddress: null,
  };

  getUserGeolocationDetails = async () => {
    var response = await fetch(
      "https://geolocation-db.com/json/697de680-a737-11ea-9820-af05f4014d91"
    );
    const data = await response.json();
    console.log("dataaa", data);
    let latlng = [data.longitude, data.latitude];
    this.setState({ coordinates: latlng });
    this.setState({ address: data.address });
  };

  // searches for new locations
  updateCoordinates = (e) => {
    e.preventDefault();

    // fetches data from our api
    fetch(
      `https://google-maps-geocoding.p.rapidapi.com/geocode/json?language=en&address=${this.state.address}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "google-maps-geocoding.p.rapidapi.com",
          "x-rapidapi-key":
            "3daff95accmshe6470f33432001bp1b0220jsn683860acd393",
        },
      }
    )
      .then((response) => response.json())
      .then((response) => {
        this.setState({ latitude: response.lat });
        this.setState({ longitude: response.long });
      })
      .catch((err) => console.log(err));
  };

  handleInputChange = (event) => {
    console.log(event.target.id);
    this.setState({ [event.target.id]: event.target.value });
  };

  onSubmit = async (event) => {
    const newPost = {
      name: this.state.name,
      address: this.state.address,
      spotType: this.state.spotType,
      description: this.state.description,
      coordinates: this.state.coordinates,
    };
    console.log("test");
    console.log(this.state.address);
    event.preventDefault();
    const res = await fetch("api/v1/spots", {
      method: "POST",
      body: JSON.stringify(newPost),
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();
    console.log(data);
  };

  //   handleClearForm(e) {

  //     e.preventDefault();
  //     this.setState({
  //       newSpot: {
  //       name: "",
  //       address: "",
  //       spotType: "",
  //       description: "",

  //       },
  //     })
  // }
  componentDidMount() {
    this.getUserGeolocationDetails();
  }

  render() {
    return (
      <div>
        <Form className="form" onSubmit={this.onSubmit}>
          <Form.Group controlId="name">
            <Form.Label>Name the Spot</Form.Label>
            <Form.Control
              type="text"
              name="name"
              placeholder="Spot Name"
              value={this.state.name}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="address">
            <Form.Label>address</Form.Label>
            <Form.Control
              type="text"
              name="address"
              placeholder="Spot Address"
              value={this.state.address}
            />
          </Form.Group>
          <Form.Group controlId="coordinates">
            <Form.Label></Form.Label>
            <Form.Control
              type="hidden"
              name="coordinates"
              placeholder="Spot coordinates"
              value={this.state.coordinates}
            />
          </Form.Group>

          <Form.Group controlId="spotType">
            <Form.Label>Spot Type</Form.Label>
            <Form.Control
              as="select"
              value={this.state.spotType}
              onChange={this.handleInputChange}
            >
              <option>street</option>
              <option>park</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="description">
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="textarea"
              name="description"
              value={this.state.description}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          {/* <FormGroup>
          <Label for="exampleFile">File</Label>
          <Input type="file" name="file" id="exampleFile" />
          <FormText color="muted">
          This is some placeholder block-level help text for the above input.
          It's a bit lighter and easily wraps to a new line.
          </FormText>
        </FormGroup> */}

          <button>Submit</button>
        </Form>
        <button onClick={this.getLocation}>Get coordinates</button>
      </div>
    );
  }
}

export default CreateSpot;
