import React from "react";
import "./CreateSpot.css";

import { Form } from "react-bootstrap";

class CreateSpot extends React.Component {
  state = {
    name: "",
    address: "",
    spotType: "",
    description: "",
    coordinates: [],
    latitude: "",
    longitude: "",
    photo: null,
  };

  //   getUserGeolocationDetails = async () => {
  //     var response = await fetch(
  //       "https://geolocation-db.com/json/697de680-a737-11ea-9820-af05f4014d91"
  //     );
  //     const data = await response.json();
  //     console.log("dataaa", data);
  //     let latlng = [data.longitude, data.latitude];
  //     this.setState({ coordinates: latlng });
  //   };

  handleInputChange = (event) => {
    console.log(event.target.id);
    this.setState({ [event.target.id]: event.target.value });
  };

  handleFileChange = (event) => {
    console.log(event.target.files[0]);
    this.setState({ photo: event.target.files[0] });
  };

  onSubmit = async (event) => {
    event.preventDefault();
    const encodedAddress = encodeURI(this.state.address);
    const geodata = await fetch(
      `https://google-maps-geocoding.p.rapidapi.com/geocode/json?language=en&address=${encodedAddress}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "google-maps-geocoding.p.rapidapi.com",
          "x-rapidapi-key":
            "3daff95accmshe6470f33432001bp1b0220jsn683860acd393",
        },
      }
    );
    const dataa = await geodata.json();
    console.log("geodata", dataa);
    if (dataa.results.length > 0)
      this.setState({ address: dataa.results[0].formatted_address });
    this.setState({ latitude: dataa.results[0].geometry.location.lat });
    this.setState({ longitude: dataa.results[0].geometry.location.lng });
    const newPost = {
      name: this.state.name,
      address: this.state.address,
      spotType: this.state.spotType,
      description: this.state.description,
      latitude: this.state.latitude,
      longitude: this.state.longitude,
      photo: this.state.photo,
    };
    console.log(newPost);

    const formData = new FormData();
    formData.append("photo", this.state.photo);
    formData.append("address", this.state.address);
    formData.append("spotType", this.state.spotType);
    formData.append("description", this.state.description);
    formData.append("latitude", this.state.latitude);
    formData.append("longitude", this.state.longitude);
    formData.append("name", this.state.name);

    const res = await fetch(`/api/v1/${this.props.userId}/spots`, {
      method: "POST",
      body: formData,
    });
    const data = await res.json();
    console.log("post", data);
    // this.props.setSpot(data);
  };

  //   handleClearForm(e) {

  //     e.preventDefault();
  //     this.setState({
  //       newSpot: {
  //       name: "",
  //       address: "",
  //       spotType: "",
  //       description: "",

  //       },
  //     })
  // }
  componentDidMount() {
    console.log(this.props.match);
  }

  render() {
    return (
      <div>
        <Form className="form" onSubmit={this.onSubmit}>
          <Form.Group controlId="name">
            <Form.Label>Name the Spot</Form.Label>
            <Form.Control
              type="text"
              name="name"
              placeholder="Spot Name"
              value={this.state.name}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="address">
            <Form.Label>Address</Form.Label>
            <Form.Control
              type="text"
              name="address"
              placeholder="Spot Address"
              value={this.state.address}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="longitude">
            <Form.Label></Form.Label>
            <Form.Control
              type="hidden"
              name="longitude"
              value={this.state.longitude}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="latitude">
            <Form.Label></Form.Label>
            <Form.Control
              type="hidden"
              name="latitude"
              value={this.state.latitude}
              onChange={this.handleInputChange}
            />
          </Form.Group>

          <Form.Group controlId="spotType">
            <Form.Label>Spot Type</Form.Label>
            <Form.Control
              as="select"
              value={this.state.spotType}
              onChange={this.handleInputChange}
            >
              <option>street</option>
              <option>park</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="description">
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="textarea"
              name="description"
              value={this.state.description}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="photo">
            <Form.File
              type="file"
              name="photo"
              label="Example file input"
              onChange={this.handleFileChange}
            />
          </Form.Group>
          {/* <FormGroup>
          <Label for="exampleFile">File</Label>
          <Input type="file" name="file" id="exampleFile" />
          <FormText color="muted">
          This is some placeholder block-level help text for the above input.
          It's a bit lighter and easily wraps to a new line.
          </FormText>
        </FormGroup> */}

          <button>Submit</button>
        </Form>
      </div>
    );
  }
}

export default CreateSpot;
