import React from "react";
import "./Account.css";
import { useHistory } from "react-router-dom";

import {
  Container,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Alert,
} from "reactstrap";

class Account extends React.Component {
  state = {
    currentName: "",
    currentEmail: "",
    name: "",
    email: "",
  };

  handleInputChange = (event) => {
    const { value, name } = event.target;
    this.setState({
      [name]: value,
      token: null,
    });
  };

  currentUser = async () => {
    const res = await fetch("/api/v1/users/me", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();
    console.log(data);
    this.setState({ currentEmail: data.data.data.email });
    this.setState({ currentName: data.data.data.name });
  };

  onSubmit = async (event) => {
    try {
      event.preventDefault();
      const res = await fetch("api/v1/users/updateMe", {
        method: "POST",
        body: JSON.stringify(this.state),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await res.json();
      console.log(data);

      this.props.setUser(data);
      // console.log(this.props.user);
    } catch (err) {
      console.log(err);
    }
  };

  componentDidMount() {
    this.currentUser();
  }

  render() {
    // console.log(this.state.data);
    return (
      <Container className="App">
        <h2>Account Settings</h2>
        <Form className="form" onSubmit={this.onSubmit}>
          <Col>
            <FormGroup>
              <Label>Name</Label>
              <Input
                type="name"
                name="name"
                id="name"
                placeholder={this.state.currentName}
                value={this.state.name}
                onChange={this.handleInputChange}
                required
              />
            </FormGroup>
          </Col>
          <Col>
            <FormGroup>
              <Label for="Email">Email</Label>
              <Input
                type="email"
                name="email"
                id="email"
                placeholder={this.state.currentEmail}
                value={this.state.email}
                onChange={this.handleInputChange}
                required
              />
            </FormGroup>
          </Col>

          <Input type="submit" value="Submit" />
        </Form>
      </Container>
    );
  }
}

export default Account;
