import React from "react";
import "./Account.css";
import { Redirect } from "react-router-dom";

import {
  Container,
  Col,
  Form,
  FormGroup,
  Label,
  Input,
  Button,
  Alert,
} from "reactstrap";

class Account extends React.Component {
  state = {
    currentName: "",
    currentEmail: "",
    name: "",
    email: "",
    passwordCurrent: "",
    password: "",
    passwordConfirm: "",
    succesfulCreate: false,
  };

  handleInputChangeData = (event) => {
    const { value, name } = event.target;
    this.setState({
      [name]: value,
      token: null,
    });
  };

  handleInputChange = (event) => {
    const { value, name } = event.target;
    this.setState({
      [name]: value,
    });
  };

  currentUser = async () => {
    const res = await fetch("/api/v1/users/me", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    const data = await res.json();
    console.log(data);
    this.setState({ currentEmail: data.data.data.email });
    this.setState({ currentName: data.data.data.name });
  };

  onSubmitData = async (event) => {
    try {
      event.preventDefault();
      const res = await fetch("api/v1/users/updateMe", {
        method: "PATCH",
        body: JSON.stringify(this.state),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await res.json();
      console.log(data);
      this.props.setUser(data);
      this.setState({ successfulCreate: true });

      // console.log(this.props.user);
    } catch (err) {
      console.log(err);
    }
  };

  onSubmitPassword = async (event) => {
    try {
      event.preventDefault();
      const res = await fetch("api/v1/users/updateMyPassword", {
        method: "PATCH",
        body: JSON.stringify(this.state),
        headers: {
          "Content-Type": "application/json",
        },
      });
      const data = await res.json();
      console.log(data);
      this.setState({ successfulCreate: true });
      // console.log(this.props.user);
    } catch (err) {
      console.log(err);
    }
  };

  componentDidMount() {
    this.currentUser();
  }

  render() {
    if (this.state.successfulCreate) {
      return <Redirect to="/" />;
    }
    // console.log(this.state.data);
    return (
      <Container className="App">
        <h2>Account Settings</h2>
        <Form className="form" onSubmit={this.onSubmitData}>
          <Col>
            <FormGroup>
              <Label>Name</Label>
              <Input
                type="name"
                name="name"
                id="name"
                placeholder={this.state.currentName}
                value={this.state.name}
                onChange={this.handleInputChangeData}
                required
              />
            </FormGroup>
          </Col>
          <Col>
            <FormGroup>
              <Label for="Email">Email</Label>
              <Input
                type="email"
                name="email"
                id="email"
                placeholder={this.state.currentEmail}
                value={this.state.email}
                onChange={this.handleInputChangeData}
                required
              />
            </FormGroup>
          </Col>

          <Input type="submit" value="Submit" />
        </Form>

        <Form className="form" onSubmit={this.onSubmitPassword}>
          <Col>
            <FormGroup>
              <Label>current Password</Label>
              <Input
                type="passwordCurrent"
                name="passwordCurrent"
                id="passwordCurrent"
                placeholder="************"
                value={this.state.passwordCurrent}
                onChange={this.handleInputChange}
                required
              />
            </FormGroup>
          </Col>
          <Col>
            <FormGroup>
              <Label for="password">New Password</Label>
              <Input
                type="password"
                name="password"
                id="password"
                placeholder="************"
                value={this.state.password}
                onChange={this.handleInputChange}
                required
              />
            </FormGroup>
          </Col>
          <Col>
            <FormGroup>
              <Label for="passwordConfirm">Confirm Password</Label>
              <Input
                type="passwordConfirm"
                name="passwordConfirm"
                id="passwordConfirm"
                placeholder="*******"
                value={this.state.passwordConfirm}
                onChange={this.handleInputChange}
                required
              />
            </FormGroup>
          </Col>

          <Input type="submit" value="Submit" />
        </Form>
      </Container>
    );
  }
}

export default Account;
