import React from "react";
import "./UpdateSpot.css";

import { Form } from "react-bootstrap";

class UpdateSpot extends React.Component {
  state = {
    editedSpot: {},
  };

  handleGetRequest = async () => {
    const response = await fetch(
      `/api/v1/spots/${this.props.match.match.params.id}`
    );
    const data = await response.json();
    console.log(data);
    this.setState({ editedSpot: data.data.spot });
  };

  setEditSpot = (spot) => {
    this.setState({ editedSpot: spot });
  };

  handleInputChange = (event) => {
    const editSpot = this.state.editedSpot;
    this.setEditSpot({
      name: editSpot.name,
      address: editSpot.address,
      spotType: editSpot.spotType,
      description: editSpot.description,
      latitude: editSpot.latitude,
      longitude: editSpot.longitude,
      photo: editSpot.photo,
      ...{ [event.target.id]: event.target.value },
    });
  };

  handleFileChange = (event) => {
    const editSpot = this.state.editedSpot;
    this.setEditSpot({
      photo: editSpot.photo,

      ...{ photo: event.target.files[0] },
    });
  };

  onSubmit = async (event) => {
    event.preventDefault();
    const encodedAddress = encodeURI(this.state.editedSpot.address);
    const geodata = await fetch(
      `https://google-maps-geocoding.p.rapidapi.com/geocode/json?language=en&address=${encodedAddress}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "google-maps-geocoding.p.rapidapi.com",
          "x-rapidapi-key":
            "3daff95accmshe6470f33432001bp1b0220jsn683860acd393",
        },
      }
    );
    const dataa = await geodata.json();
    console.log("geodata", dataa);
    if (dataa.results.length > 0)
      this.setState({ address: dataa.results[0].formatted_address });
    this.setState({ latitude: dataa.results[0].geometry.location.lat });
    this.setState({ longitude: dataa.results[0].geometry.location.lng });
    const newPost = {
      name: this.state.name,
      address: this.state.address,
      spotType: this.state.spotType,
      description: this.state.description,
      latitude: this.state.latitude,
      longitude: this.state.longitude,
      photo: this.state.photo,
    };
    console.log(newPost);

    const formData = new FormData();
    formData.append("photo", this.state.photo);
    formData.append("address", this.state.address);
    formData.append("spotType", this.state.spotType);
    formData.append("description", this.state.description);
    formData.append("latitude", this.state.latitude);
    formData.append("longitude", this.state.longitude);
    formData.append("name", this.state.name);

    const res = await fetch(
      `/api/v1/spots/${this.props.match.match.params.id}`,
      {
        method: "PATCH",
        body: formData,
      }
    );
    const data = await res.json();
    console.log("patch", data);
  };

  componentDidMount() {
    this.handleGetRequest();
  }

  render() {
    console.log("match", this.props.match.match.params.id);
    console.log("spot details", this.state.editedSpot);

    return (
      <div className="formContainer">
        <Form className="formm" onSubmit={this.onSubmit}>
          <Form.Group controlId="name">
            <Form.Label>Name the Spot</Form.Label>
            <Form.Control
              type="text"
              name="name"
              defaultValue={this.state.editedSpot.name}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="address">
            <Form.Label>Address</Form.Label>
            <Form.Control
              type="text"
              name="address"
              defaultValue={this.state.editedSpot.address}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="longitude">
            <Form.Label></Form.Label>
            <Form.Control
              type="hidden"
              name="longitude"
              defaultValue={this.state.editedSpot.longitude}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="latitude">
            <Form.Label></Form.Label>
            <Form.Control
              type="hidden"
              name="latitude"
              defaultValue={this.state.editedSpot.latitude}
              onChange={this.handleInputChange}
            />
          </Form.Group>

          <Form.Group controlId="spotType">
            <Form.Label>Spot Type</Form.Label>
            <Form.Control
              as="select"
              defaultValue={this.state.editedSpot.spotType}
              onChange={this.handleInputChange}
            >
              <option>street</option>
              <option>park</option>
            </Form.Control>
          </Form.Group>
          <Form.Group controlId="description">
            <Form.Label>Description</Form.Label>
            <Form.Control
              type="textarea"
              name="description"
              defaultValue={this.state.editedSpot.description}
              onChange={this.handleInputChange}
            />
          </Form.Group>
          <Form.Group controlId="photo">
            <Form.File
              type="file"
              name="photo"
              defaultValue={this.state.editedSpot.photo}
              label="Example file input"
              onChange={this.handleFileChange}
            />
          </Form.Group>
          {/* <FormGroup>
          <Label for="exampleFile">File</Label>
          <Input type="file" name="file" id="exampleFile" />
          <FormText color="muted">
          This is some placeholder block-level help text for the above input.
          It's a bit lighter and easily wraps to a new line.
          </FormText>
        </FormGroup> */}

          <button>Submit</button>
        </Form>
      </div>
    );
  }
}

export default UpdateSpot;
