import React from "react";
import "../../pages/Spot/Spot.css";
import { Link, Redirect } from "react-router-dom";
import { Container, Row, Col } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { getDistance } from "geolib";

class Spot extends React.Component {
  state = {
    spot: { location: {} },
    details: null,
    succesfulDelete: false,
  };

  deleteSpot = async () => {
    await fetch(`/api/v1/spots/${this.state.spot.id}`, {
      method: "DELETE",
    });
    this.setState({ successfulDelete: true });
  };

  handleGetRequest = async () => {
    const response = await fetch(`/api/v1/spots/${this.props.match.params.id}`);
    const data = await response.json();
    console.log(data);
    this.setState({ spot: data.data.spot });
    // this.props.setSpot(data);
  };

  componentDidMount() {
    this.handleGetRequest();
  }
  render() {
    console.log(this.state.spot);
    if (this.state.successfulDelete) {
      return <Redirect to="/" />;
    }
    console.log("mySpot", this.state.spot);
    return (
      <div className="mainContainer">
        <div className="name">{this.state.spot.name}</div>
        <div className="container2">
          <img
            className="spotImg"
            src={`/static/img/spots/${this.state.spot.photo}`}
          ></img>
        </div>

        <span className="detailsHead">Details</span>
        <div className="type">Spot Type: {this.state.spot.spotType}</div>

        <div className="spot-address">Address: {this.state.spot.address}</div>

        <span className="descriptionHead">Description</span>
        <div className="description">{this.state.spot.description}</div>
      </div>
    );
  }
}

export default Spot;
